import re
from typing import List, Dict

class DetailAction:
    def __init__(self):
        self.json_object = None
        self.text = ""

    def execute(self, context, body):
        self.json_object = context
        self.text = str(body)
        self.process_reg()
        self.process_substr()
        before_value = self.json_object.get_string("beforeValue")
        if before_value and self.text:
            self.text = before_value + self.text
        after_value = self.json_object.get_string("afterValue")
        if after_value and self.text:
            self.text += after_value
        self.process_filter_tags()
        self.process_filter_atts()
        self.process_filter_all_att()
        if self.json_object.get_boolean("sbcFlag"):
            self.text = self.sbc2dbc(self.text)
        if self.json_object.get_boolean("plainFlag"):
            self.text = PlainExtractor().process(self.text)
        if self.json_object.get_boolean("stripFlag"):
            self.text = self.text.strip()
        if self.json_object.get_boolean("unicodeFlag"):
            self.text = self.unescape_java(self.text)
        self.process_replace()
        return NodeVariable(self.text)

    def process_substr(self):
        start_pos = 0
        start_str = self.json_object.get_string("startStr")
        if start_str:
            contain_start = self.json_object.get_boolean("containStart")
            start_to_border = self.json_object.get_boolean("startToBorder")
            start_pos = self.text.find(start_str)
            miss_flag = False
            if start_pos == -1:
                miss_flag = True
                if start_to_border:
                    start_pos = 0
            if start_pos == -1:
                self.text = ""
            elif not contain_start:
                start_pos += miss_flag * 0 + (not miss_flag) * len(start_str)

        end_pos = len(self.text)
        end_str = self.json_object.get_string("endStr")
        if end_str:
            contain_end = self.json_object.get_boolean("containEnd")
            end_to_border = self.json_object.get_boolean("endToBorder")
            end_pos = self.text.find(end_str, start_pos)
            miss_flag2 = False
            if end_pos == -1:
                miss_flag2 = True
                if end_to_border:
                    end_pos = len(self.text)
            if end_pos == -1:
                self.text = ""
            elif contain_end:
                if not miss_flag2:
                    end_pos += len(end_str)

        self.text = self.text[start_pos:end_pos]

    def process_reg(self):
        reg_value = self.json_object.get_string("regValue2")
        reg_value2_result = self.json_object.get_string("regValue2Result")
        if reg_value:
            matcher = re.compile(reg_value, re.IGNORECASE | re.DOTALL)
            match = matcher.search(self.text)
            if not match:
                self.text = ""
            elif reg_value2_result:
                self.text = reg_value2_result
                for i in range(match.re.groups + 1):
                    self.text = self.text.replace("${_" + str(i) + "}", match.group(i))
            else:
                self.text = match.group(0)

    def process_filter_tags(self):
        filter_tags = self.json_object.get_string("filterTags")
        if filter_tags:
            tags = filter_tags.split(',')
            for tag in tags:
                if tag.lower() == 'b':
                    self.text = re.sub(r"<[/]?b>", "", self.text, flags=re.IGNORECASE)
                else:
                    self.text = re.sub(r"<[/]?" + re.escape(tag) + r".*?[/]?>", "", self.text, flags=re.IGNORECASE)

    def process_filter_atts(self):
        filter_atts = self.json_object.get_string("filterAtts")
        if filter_atts:
            atts = filter_atts.split(',')
            for att in atts:
                self.text = re.sub(r"\s*" + re.escape(att) + r"\s*=\s*[\"'].+?[\"']", "", self.text, flags=re.IGNORECASE)

    def process_filter_all_att(self):
        if self.json_object.get_boolean("filterFlag"):
            buffer = []
            matcher = re.compile(r"<[a-z]+\s+(.*?)\s*/?>", flags=re.IGNORECASE | re.DOTALL)
            last_end = 0
            for match in matcher.finditer(self.text):
                buffer.append(self.text[last_end:match.start()])
                buffer.append(match.group().replace(match.group(1), ""))
                last_end = match.end()
            buffer.append(self.text[last_end:])
            self.text = "".join(buffer)

    def process_replace(self):
        replaces = self.json_object.get_json_array("replaces")
        for i in range(len(replaces)):
            replace_json_object = replaces[i]
            search_value = replace_json_object.get_string("searchValue")
            replace_value = replace_json_object.get_string("replaceValue")
            if replace_value == "[EMPTY]":
                replace_value = ""
            search_flag = replace_json_object.get_boolean("searchFlag")
            replace_flag = replace_json_object.get_boolean("replaceFlag")
            if search_flag:
                if replace_flag:
                    self.text = re.sub(search_value, replace_value, self.text, flags=re.IGNORECASE | re.DOTALL)
                else:
                    self.text = re.sub(search_value, replace_value, self.text, count=1, flags=re.IGNORECASE | re.DOTALL)
            elif replace_flag:
                self.text = self.text.replace(search_value, replace_value)
            else:
                self.text = self.text.replace(search_value, replace_value, 1)

    @staticmethod
    def sbc2dbc(text):
        return text.translate(str.maketrans({
            '！': '!', '＠': '@', '＃': '#', '＄': '$', '％': '%', '＆': '&', '＊': '*', '（': '(',
            '）': ')', '－': '-', '＝': '=', '＋': '+', '＜': '<', '＞': '>', '｛': '{', '｝': '}',
            '［': '[', '］': ']', '｜': '|', '＼': '\\', '＾': '^', '～': '~', '∶': ':', '；': ';',
            '＂': '"', '｀': '`', '＜': '<', '＞': '>', '＿': '_', '～': '~', '？': '?', '！': '!',
            '＠': '@', '＃': '#', '＄': '$', '％': '%', '＆': '&', '＊': '*', '（': '(',
            '）': ')', '－': '-', '＝': '=', '＋': '+', '＜': '<', '＞': '>', '｛': '{', '｝': '}',
            '［': '[', '］': ']', '｜': '|', '＼': '\\', '＾': '^', '～': '~', '∶': ':', '；': ';',
            '＂': '"', '｀': '`'
        }))

    @staticmethod
    def unescape_java(text):
        return text.encode('utf-8').decode('unicode_escape')

class NodeVariable:
    def __init__(self, value):
        self.value = value

class PlainExtractor:
    def process(self, text):
        # Implement the plain extractor logic here
        return text.strip()

class IJsonObject:
    def __init__(self, data: Dict):
        self.data = data

    def get_string(self, key):
        return self.data.get(key, "")

    def get_boolean(self, key):
        return bool(self.data.get(key, False))

    def get_json_array(self, key):
        return self.data.get(key, [])

class IJsonArray:
    def __init__(self, data: List):
        self.data = data

    def size(self):
        return len(self.data)

    def get_json_object(self, index):
        return IJsonObject(self.data[index])
